import { useEffect, useState } from "react";
import useInput from "../../Hooks/useInput";
import { useNavigate } from "react-router-dom";
import { useDispatch } from "react-redux";
import { loginActions } from "../../Store/loginSlice";

const LoginForm = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const [afterPasswordError, setafterPasswordError] = useState(false);

  const [sendDetail, setSendDetail] = useState(null);
  const {
    value: enteredPassword,
    hasError: passwordInputError,
    isValid: enteredPasswordIsValid,
    valueChangeHandler: passwordChangeHandler,
    isTouchHandler: passwordBlurHandler,
    reset: resetPassword,
  } = useInput((value) => value.trim !== "");
  const {
    value: enteredEmail,
    hasError: emailInputError,
    isValid: enteredEmailIsValid,
    valueChangeHandler: emailChangeHandler,
    isTouchHandler: emailBlurHandler,
    reset: resetEmail,
  } = useInput((value) => value.trim !== "");

  const formSubmitHandler = (event) => {
    event.preventDefault();

    if (enteredEmailIsValid && enteredPasswordIsValid) {
      setSendDetail(true);
    }
  };

  useEffect(() => {
    if (!sendDetail) {
      return;
    }
    const upload = async () => {
      const url = `http://localhost:3003/users/login`;

      const response = await fetch(url, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ enteredEmail, enteredPassword }),
      });

      const data = await response.json();

      if (data.message === "successfull") {
        localStorage.setItem("token")
         console.log(data.message);
        dispatch(loginActions.login());
        navigate("/home");
        setafterPasswordError(true);
      } else if (data.message === "Invalid Password") {
        setafterPasswordError(true);
      }
    };
    upload().catch((error) => {});
  }, [
    sendDetail,
    enteredEmail,
    enteredPassword,
    setafterPasswordError,
    navigate,
    dispatch,
  ]);

  return (
    <section className="max-container w-full flex flex-col justify-center gap-10 pt-16 xl:flex-row">
      <form
        onSubmit={formSubmitHandler}
        className="relative xl:3/5 flex flex-col justify-center items-center max-xl:padding-x max-container align-middle mt-12 pt-12"
      >
        <div className="flex flex-col justify-center pt-5">
          <label htmlFor="name" className="pb-5 items-start justify-start">
            Enter your email
          </label>
          <input
            type="name"
            id="name"
            placeholder="john@gmail.com"
            value={enteredEmail}
            onChange={emailChangeHandler}
            onBlur={emailBlurHandler}
            className={`border-solid border-2 rounded-md items-center px-10 py-3`}
          ></input>
        </div>
        {emailInputError && (
          <p className=" -my-5 text-center text-red-400 pb-5">
            Name can not be empty
          </p>
        )}
        <div className="flex flex-col justify-center pt-5">
          <label htmlFor="name" className="pb-5 items-start justify-start">
            Enter your password
          </label>
          <input
            type="text"
            id="password"
            value={enteredPassword}
            placeholder="password@123"
            onChange={passwordChangeHandler}
            onBlur={passwordBlurHandler}
            className={`border-solid border-2 rounded-md items-center px-10 py-3 `}
          ></input>
        </div>
        {passwordInputError && (
          <p className=" -my-5 text-center text-red-400 pb-5">
            Password can not be empty
          </p>
        )}
        {afterPasswordError && <p className="">Password is Incorrect</p>}
        <button
          className=" bg-[#dc143c] text-center text-slate-200 rounded-full font-palanquin font-medium text-lg px-12 py-4 mt-6 mx-10"
          type="submit"
        >
          Sumbit
        </button>
      </form>
    </section>
  );
};

export default LoginForm;
