import React, { Fragment, useEffect, useState } from "react";
import { Link } from "react-router-dom";
import Header from "../components/common/Header/Header";
import Footer from "../components/common/Footer/Footer";
import UserMeetupCard from "../components/UserMeetup/UserMeetupCard";
const UserEvent = () => {
  const [meetups, setMeetup] = useState("");
  const token = localStorage.getItem("token");

  useEffect(() => {
    const fetchData = async () => {
      const url = "http://localhost:3003/meetup/get-meetup";
      try {
        const response = await fetch(url, {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token} `,
          },
        });
        if (!response.ok) {
          console.log("something went wrong")
        }
        const data = await response.json();
        setMeetup(data.allmeetup);



      } catch (error) {
        console.log(error)
      }
    };
    fetchData().catch((error) => {
      console.log(error);
    })
  }, [token]);


  return (
    <Fragment>
      <Header />
      <div class="w-full flex xl:flex-row flex-col justify-center gap-10 max-container pt-12">
        <div className="flex flex-col justify-center items-start w-full max-xl:padding-x">
          <div className="relative mt-8 flex-1 justify-center w-full max-xl:padding-x max-xl:py-40">
            <div className="flex gl:flex-row gl:gap-10">
              <div className="relative mt-8 flex-1 justify-center w-full max-xl:padding-x max-xl:py-40">
                <div className="">
                  <h1 class="">Back</h1>
                  <Link to="/home"></Link>
                </div>
                <div className=""></div>
              </div>
              <div className="relative mt-8 flex-1 justify-center w-full max-xl:padding-x max-xl:py-40 ">
                <h1>Your Events</h1>
                {meetups.map(meetup => (
                  <UserMeetupCard  key={meetup.id} name={meetup.name} />
                )
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </Fragment>
  );
};

export default UserEvent;
