import { Link } from "react-router-dom";

import PreFooter from "./PreFooter";
import { footerlinks } from "../constants";

const Footer = () => {
  return (
    <footer className=" bg-zinc-800">
      <div className="max-container">
        <PreFooter />
        <div className="flex flex-col justify-between sm:flex-row">
          <div className="flex justify-between items-start gap-20 flex-wrap max-lg:flex-col">
            <div className="flex flex-col items-start">
              <a href="/">
                <h2 className="">Meetup</h2>
              </a>
              <p className="mt-6 text-base leading-7 font-montserrat text-white-400 sm:max-w-sm">
                Get shoes ready for the new term at your nearest Nike store.
                Find Your perfect Size In Store. Get Rewards
              </p>
              {/* <div className="flex items-center gap-5 mt-8">
                {socialMedia.map((icon) => (
                  <div
                    className="flex justify-center items-center w-12 h-12 bg-white rounded-full"
                    key={icon.alt}
                  >
                    <img src={icon.src} alt={icon.alt} width={24} height={24} />
                  </div> */}
              {/* ))} */}
            </div>
          </div>
        </div>
        <nav className="flex flex-1 justify-between lg:gap-10 gap-20 flex-wrap">
          {footerlinks.map((section) => (
            <nav id={section.title}>
              <h4 className="text-white">{section.title}</h4>
              <ul>
                {section.links.map((link) => (
                  <li
                    className="mt-3 text-white font-montserrat text-base leading-normal hover:text-orange-800"
                    key={link.name}
                  >
                    <Link to={link.page}>{link.name}</Link>
                  </li>
                ))}
              </ul>
            </nav>
          ))}
        </nav>
      </div>
    </footer>
  );
};

export default Footer;
