import { Link } from "react-router-dom";

import NavBar from "./NavBar";
import { useSelector } from "react-redux";

const Header = () => {
  const isLogin = useSelector((state) => state.login.isAuthenthicated);
  console.log(isLogin);
  return (
    <header className="flex bg-blue-600 p-5 text-white shadow-md">
      <div className="container mx-auto flex justify-between items-center">
        <h1 className="text-3xl font-semibold">Meetup</h1>
      </div>
      <NavBar></NavBar>
      <div className="flex">
        {!isLogin && (
          <Link
            to="/login"
            className="mr-4 text-blue-600 bg-white px-4 py-2 rounded"
          >
            Login
          </Link>
        )}
        {!isLogin && (
          <button className="mr-4 bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded">
            <Link to="/signup">Sing Up</Link>
          </button>
        )}
        {isLogin && (
          <button className="mr-4 bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded">
            Buy Tickets
          </button>
        )}
      </div>
    </header>
  );
};
export default Header;
