import useInput from "../../Hooks/useInput";

const LoginForm = () => {

    const {value:enteredName,hasError:nameInputError,isValid :enteredNameIsValid, valueChangeHandler:nameChangeHandler,}=useInput(value=> value.trim!=='')

    const formSubmitHandler = (event) => { };



  return (
    <section className="max-container w-full flex flex-col justify-center gap-10 pt-16 xl:flex-row">
      <form
        onSubmit={formSubmitHandler}
        className="relative xl:3/5 flex flex-col justify-center items-center max-xl:padding-x max-container align-middle mt-12 pt-12"
      >
        <div className="flex flex-col justify-center pt-5">
          <label htmlFor="name" className="pb-5 items-start justify-start">
            Enter your name
          </label>
          <input
            type="name"
            id="name"
            placeholder="john@gmail.com"
            className={`border-solid border-2 rounded-md items-center px-10 py-3`}
          ></input>
        </div>
        <div className="flex flex-col justify-center pt-5">
          <label htmlFor="name" className="pb-5 items-start justify-start">
            Enter your password
          </label>
          <input
            type="text"
            id="password"
            placeholder="password@123"
            className={`border-solid border-2 rounded-md items-center px-10 py-3 `}
          ></input>
        </div>
        <button
          className=" bg-[#dc143c] text-center text-slate-200 rounded-full font-palanquin font-medium text-lg px-12 py-4 mt-6 mx-10"
          type="submit"
        >
          Sumbit
        </button>
      </form>
    </section>
  );
};

export default LoginForm;
