import { FormControl, FormLabel, TextField } from "@material-ui/core";

import classes from "./MeetupForm.module.css";
import useInput from "../../Hooks/useInput";
import { useState,useEffect } from "react";

const MeetUpForm = () => {
  const [shouldUpload, setShouldUpload] = useState(null); // to decide if upload should be done or not
  const {
    value: enteredName,
    isValid: enteredNameIsValid,
    hasError: nameInputError,
    valueChangeHandler: nameChangeHandler,
    isTouchHandler: nameBlurHandler,
    reset: resetName,
  } = useInput((value) => value.trim() !== "");
  const {
    value: enteredAddress,
    isValid: enteredAddressIsValid,
    hasError: addressInputError,
    valueChangeHandler: addressChangeHandler,
    isTouchHandler: addressBlurHandler,
    reset: resetAddress,
  } = useInput((value) => value.trim() !== "");

  const {
    value: enteredDescription,
    isValid: enteredDescriptionIsValid,
    hasError: discriptionInputError,
    valueChangeHandler: discriptionChangeHandler,
    isTouchHandler: discriptionBlurHandler,
    reset: resetDescription,
  } = useInput((value) => value.trim() !== "");

  const sumbitHandler = (event) => {
    if (
      //checking for form validity
      enteredAddressIsValid &&
      enteredDescriptionIsValid &&
      enteredNameIsValid
    ) {
      setShouldUpload(true);
    }

    resetName();
    resetAddress();
    resetDescription();
  };
    useEffect(() => { //sending the data to backend

        if (!shouldUpload) return;

        const uploadData = async () => {
            const url = "http://localhost:3003/add-user";
            try {
                const respone = await fetch(url, {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body:JSON.stringify({enteredAddress,enteredName,enteredDescription})
                });
            } catch (error) {

            }

        }
  });
  return (
    <form onSubmit={sumbitHandler} className={classes.container}>
      <FormLabel>Your Name</FormLabel>
      <TextField
        type="text"
        size="medium"
        className={classes.container_item}
        value={enteredName}
        color={nameInputError ? "primary" : "secondary"}
        variant="outlined"
        placeholder="John Smith"
        onChange={nameChangeHandler}
        onBlur={nameBlurHandler}
      ></TextField>
      <FormLabel>Event Address</FormLabel>
      <TextField
        type="text"
        size="medium"
        value={enteredAddress}
        color={enteredAddressIsValid ? "primary" : "secondary"}
        variant="outlined"
        onChange={addressChangeHandler}
        onBlur={addressBlurHandler}
      ></TextField>
      <FormLabel>Event's Poster</FormLabel>
      <TextField
        type="image"
        size="medium"
        alt="event poster image"
        variant="outlined"
      ></TextField>
      <FormLabel>Event's Date</FormLabel>
      <TextField type="date" size="normal" variant="outlined"></TextField>
      <FormLabel>Enter Event's Description</FormLabel>
      <TextField
        type="text"
        size="medium"
        value={enteredDescription}
        variant="outlined"
        color={enteredDescriptionIsValid ? "primary" : "secondary"}
        onChange={discriptionChangeHandler}
        onBlur={discriptionBlurHandler}
      ></TextField>
      <button type="submit">Add Event</button>
    </form>
  );
};

export default MeetUpForm;
