import { Link } from "react-router-dom";

import NavBar from "./NavBar";
import { useSelector } from "react-redux";

const Header = () => {
  const isLogin = useSelector((state) => state.login.isAuthenthicated);
  console.log(isLogin);
  return (
    <header className="padding-x py-3 bg-[#f5deb3] hover:bg-transparent absolute z-10 w-full text-white shadow-md">
      <nav className="flex justify-between items-center max-container">
        <div className="mx-auto flex items-center justify-start">
          <h1 className=" text-slate-950 text-3xl font-semibold">Meetup</h1>
        </div>
        <NavBar></NavBar>

      <div className="flex las">
        {!isLogin && (
          <Link
            to="/login"
            className="mr-4 text-blue-600 bg-white px-4 py-2 rounded"
          >
            Login
          </Link>
        )}
        {!isLogin && (
          <button className="mr-4 bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded">
            <Link to="/signup">Sing Up</Link>
          </button>
        )}
        {isLogin && (
          <button className="mr-4 bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded">
            Buy Tickets
          </button>
        )}
      </div>
      </nav>
    </header>
  );
};
export default Header;
