import { useDispatch } from "react-redux";
import { useNavigate } from "react-router-dom";
import useInput from "../../Hooks/useInput";

import { loginActions } from "../../Store/loginSlice";

const SignUpForm = (props) => {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const {
    value: enteredName,
    isValid: enteredNameIsValid,
    hasError: nameInputError,
    valueChangeHandler: nameChangeHandler,
    isTouchHandler: nameBlurHandler,
    reset: resetName,
  } = useInput((value) => value.trim() !== "");
  const {
    value: enteredEmail,
    isValid: enteredEmailIsValid,
    hasError: emailInputError,
    valueChangeHandler: emailChangeHandler,
    isTouchHandler: emailBlurHandler,
    reset: resetEmail,
  } = useInput((value) => value.includes("@"));

  const {
    value: enteredPassword,
    isValid: enteredPasswordIsValid,
    hasError: passwordInputError,
    valueChangeHandler: passwordChangeHandler,
    isTouchHandler: passwordBlurHandler,
    reset: resetPassword,
  } = useInput((value) => value.trim() !== "" && value.length > 6);

  const formSubmitHandler = async (event) => {
    event.preventDefault();

    const url = `http://localhost:3003/users/add-user`;

    const response = await fetch(url, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ enteredEmail, enteredName, enteredPassword }),
    });
    const data = await response.json();
    if (data) {
      console.log(data);
    }

    dispatch(loginActions.login());
    navigate("/home");
    resetName();
    resetEmail();
    resetPassword();
  };

  const nameInputClass = nameInputError
    ? "border-red-500 bg-red-100"
    : "border-slate-700";
  const emailInputClass = emailInputError
    ? "border-red-500 bg-red-100"
    : "border-slate-700";
  const passwordInputClass = passwordInputError
    ? "border-red-500 bg-red-100"
    : "border-slate-700";
  return (
    <section className="w-full flex xl:flex-row flex-col justify-center gap-10 max-container pt-16 bg-white">
      <form
        onSubmit={formSubmitHandler}
        className=" relative xl:3/5 flex flex-col justify-center items-center max-xl:padding-x max-container align-middle mt-12 pt-12"
      >
        <div className="flex flex-col justify-center pt-5 ">
          <label htmlFor="name" className="pb-5 items-start justify-start ">
            Enter your Name
          </label>
          <input
            className={`border-solid ${nameInputClass} border-2 rounded-md items-center `}
            type="name"
            id="name"
            name="name"
            placeholder="John Smith"
            value={enteredName}
            onChange={nameChangeHandler}
            onBlur={nameBlurHandler}
          />
        </div>
        {nameInputError && (
          <p className=" -my-5 text-center text-red-400 pb-5">
            Name can not be empty
          </p>
        )}

        <div className="flex flex-col justify-center pt-5">
          <label htmlFor="email" className="pb-5 items-start justify-start">
            Enter your Email
          </label>
          <input
            className={`border-solid ${emailInputClass} border-2 rounded-md items-center `}
            type="email"
            id="email"
            name="email"
            placeholder="John@gmail.com"
            value={enteredEmail}
            onChange={emailChangeHandler}
            onBlur={emailBlurHandler}
          />
          {emailInputError && (
            <p className=" text-center text-red-400 pb-2 my-2">
              Email can not be empty
            </p>
          )}
        </div>
        <div className="flex flex-col justify-center pt-5">
          <label htmlFor="password" className="pb-5 items-start justify-start">
            Enter your Password
          </label>
          <input
            className={`border-solid ${passwordInputClass} border-2 rounded-md items-center `}
            type="password"
            id="password"
            name="password"
            placeholder="password"
            value={enteredPassword}
            onChange={passwordChangeHandler}
            onBlur={passwordBlurHandler}
          />
          {passwordInputError && (
            <p className=" text-center text-red-400 pb-2 my-2">
              Password can not be empty 
            </p>
          )}
        </div>

        <button
          className=" bg-[#dc143c] text-center text-slate-200 rounded-full font-palanquin font-medium text-lg px-6 py-4 mt-8"
          type="submit"
        >
          Sumbit
        </button>
      </form>
    </section>
  );
};
export default SignUpForm;
