import { FormLabel, TextField } from "@material-ui/core";

import classes from "./MeetupForm.module.css";
import useInput from "../../Hooks/useInput";
import { useState, useEffect } from "react";

const MeetUpForm = () => {
  const token=localStorage.getItem('token')
  const [shouldUpload, setShouldUpload] = useState(null); // to decide if upload should be done or not
  const {
    value: enteredName,
    isValid: enteredNameIsValid,
    hasError: nameInputError,
    valueChangeHandler: nameChangeHandler,
    isTouchHandler: nameBlurHandler,
    reset: resetName,
  } = useInput((value) => value.trim() !== "");
  const {
    value: enteredAddress,
    isValid: enteredAddressIsValid,
    hasError: addressInputError,
    valueChangeHandler: addressChangeHandler,
    isTouchHandler: addressBlurHandler,
    reset: resetAddress,
  } = useInput((value) => value.trim() !== "");

  const {
    value: enteredDescription,
    isValid: enteredDescriptionIsValid,
    hasError: discriptionInputError,
    valueChangeHandler: descriptionChangeHandler,
    isTouchHandler: descriptionBlurHandler,
    reset: resetDescription,
  } = useInput((value) => value.trim() !== "");

  const sumbitHandler = (event) => {
    event.preventDefault();
    console.log(enteredAddress, enteredDescription, enteredName);
    if (
      //checking for form validity
      enteredAddressIsValid &&
      enteredDescriptionIsValid &&
      enteredNameIsValid
    ) {
      setShouldUpload(true);
    } 

  };
  useEffect(() => {
    //sending the data to backend

    if (!shouldUpload) return;

    const uploadData = async () => {
      const url = "http://localhost:3003/add-meetup";
      try {
        console.log(enteredAddress + "999");
        const response = await fetch(url, {
          method: "POST",
          headers: { "Content-Type": "application/json", Authorization:`Bearer ${token}` },
          body: JSON.stringify({
            enteredAddress,
            enteredName,
            enteredDescription,
          }),
        });

        const data = await response.json();
        console.log(data.message)
      } catch (error) {
        console.log("Error file loading", error);
      }
      setShouldUpload(false);
    };
    uploadData();
  }, [shouldUpload, enteredAddress, enteredName, enteredDescription,token]);
  return (
    <form
      onSubmit={sumbitHandler}
      className="relative xl:3/5 flex flex-col justify-center max-xl:padding-x max-container align-middle gap-5 mt-12 pt-12"
    >
      <div class="flex flex-col">
        <FormLabel class="items-start justify-start text-start font-palanquin pb-2">
          Your Name
        </FormLabel>
        <TextField
          type="text"
          size="medium"
          class="items-center"
          value={enteredName}
          color={nameInputError ? "primary" : "secondary"}
          variant="outlined"
          placeholder="John Smith"
          onChange={nameChangeHandler}
          onBlur={nameBlurHandler}
        ></TextField>
      </div>
      <div class="flex flex-col">
        <FormLabel class="items-start justify-start text-start font-palanquin pb-2">
          Event Address
        </FormLabel>
        <TextField
          type="text"
          size="medium"
          value={enteredAddress}
          color={enteredAddressIsValid ? "primary" : "secondary"}
          variant="outlined"
          onChange={addressChangeHandler}
          onBlur={addressBlurHandler}
        ></TextField>
      </div>
      {/* <FormLabel>Event's Poster</FormLabel>
      <TextField
        type="image"
        size="medium"
        alt="event poster image"
        variant="outlined"
      ></TextField> */}
      {/* <FormLabel>Event's Date</FormLabel>
      <TextField type="date" size="normal" variant="outlined"></TextField> */}
      <div class="flex flex-col">
      <FormLabel class="items-start justify-start text-start font-palanquin pb-2">
        Enter Event's Description
      </FormLabel>
        <TextField
          type="text"
          size="medium"
          value={enteredDescription}
          variant="outlined"
          color={enteredDescriptionIsValid ? "primary" : "secondary"}
          onChange={descriptionChangeHandler}
          onBlur={descriptionBlurHandler}
        ></TextField>
      </div>
      <button type="submit" class="rounded-s-full border-y-red-500 ">Add Event</button>
    </form>
  );
};

export default MeetUpForm;
