import { useDispatch } from "react-redux"; 
import { useNavigate } from "react-router-dom";
import useInput from "../../Hooks/useInput";
import classes from "./SignUpForm.module.css";
import { loginActions } from "../../Store/loginSlice";
import { useEffect } from "react";

const SignUpForm = (props) => {

  const dispatch = useDispatch();
  const navigate = useNavigate();


  const {
    value: enteredName,
    isValid: enteredNameIsValid,
    hasError: nameInputError,
    valueChangeHandler: nameChangeHandler,
    isTouchHandler: nameBlurHandler,
    reset: resetName,
  } = useInput((value) => value.trim() !== "");
  const {
    value: enteredEmail,
    isValid: enteredEmailIsValid,
    hasError: emailInputError,
    valueChangeHandler: emailChangeHandler,
    isTouchHandler: emailBlurHandler,
    reset: resetEmail,
  } = useInput((value) => value.includes("@"));

  const {
    value: enteredPassword,
    isValid: enteredPasswordIsValid,
    hasError: passwordInputError,
    valueChangeHandler: passwordChangeHandler,
    isTouchHandler: passwordBlurHandler,
    reset: resetPassword,
  } = useInput((value) => value.trim() !== "" && value.length > 6);

  const formSubmitHandler = (event) => {
    event.preventDefault();

    const url = `/api/add-product`;

      fetch(url, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ enteredName, enteredPassword, enteredEmail }),
      })
        .then((r) => r.json())
        .then((data) => console.log(data));


    dispatch(loginActions.login());
    navigate('/home');
    resetName();
    resetEmail();
    resetPassword();
  };

  const nameInputClass = nameInputError ? classes.invalid : "";
  const emailInputClass = emailInputError ? classes.invalid : "";
  const passwordInputClass = passwordInputError ? classes.invalid : "";
  return (
    <div className={classes.container}>
      <form onSubmit={formSubmitHandler}>
        <div className={classes.form_element}>
          <label htmlFor="name"> Enter your Name</label>
          <input
            className={nameInputClass}
            type="name"
            id="name"
            name="name"
            placeholder="John Smith"
            value={enteredName}
            onChange={nameChangeHandler}
            onBlur={nameBlurHandler}
          />
        </div>
        {nameInputError && (
          <p className={classes.error_text}>Name can not be empty</p>
        )}

        <div className={classes.form_element}>
          <label htmlFor="email"> Enter your Email</label>
          <input
            className={emailInputClass}
            type="email"
            id="email"
            name="email"
            placeholder="John@gmail.com"
            value={enteredEmail}
            onChange={emailChangeHandler}
            onBlur={emailBlurHandler}
          />
          {emailInputError && (
            <p className={classes.error_text}>Email can not be empty</p>
          )}
        </div>
        <div className={classes.form_element}>
          <label htmlFor="password"> Enter your Password</label>
          <input
            className={passwordInputClass}
            type="password"
            id="password"
            name="password"
            placeholder="your@password"
            value={enteredPassword}
            onChange={passwordChangeHandler}
            onBlur={passwordBlurHandler}
          />
          {passwordInputError && (
            <p className={classes.error_text}>Email can not be empty</p>
          )}
        </div>

        <button className={classes.btn_round} type="submit">
          Sumbit
        </button>
      </form>
    </div>
  );
};
export default SignUpForm;
